import React, { useRef } from "react";
import { fetchCity } from "../../../redux/actions/actions";
import { useDispatch } from "react-redux";
import videoBg from "../../../assets/bgvideo.mp4";

export default function LandingPage() {
  const [city, setCity] = React.useState("");
  const dispatch = useDispatch();
  const video = useRef<HTMLVideoElement>();

  const playVideo = (event: any) => {
    video.current && video.current.play();
  };

  const makeApiCall = (str: string) => {
    dispatch(fetchCity(str) as any);
  };

  return (
    <div>
      <input placeholder="5000" onChange={(e) => setCity(e.target.value)} />
      <button onClick={() => makeApiCall(city)}>aaaaaaaaaaaaaaaaaaa</button>
      <div>
        <video ref={video} loop src={videoBg} />
      </div>
    </div>
  );
}
