import { ACTION_TYPES, Action } from "../actionTypes/actionTypes";

export interface CityState {
  city: any[];
  loading: boolean;
  error: string;
}

const initialState = {
  city: [],
  loading: false,
  error: "",
} as CityState;

export const weatherReducer = (
  state: CityState = initialState,
  action: Action
) => {
  switch (action.type) {
    case ACTION_TYPES.FETCH_POSTS_REQUEST: {
      return {
        ...state,
        loading: true,
      };
    }
    case ACTION_TYPES.FETCH_POSTS_SUCCESS: {
      return {
        loading: false,
        city: action.payload,
        error: "",
      };
    }
    case ACTION_TYPES.FETCH_POSTS_FAILURE: {
      return {
        loading: false,
        city: [],
        error: action.payload,
      };
    }

    case ACTION_TYPES.DELETE_ONE_POST: {
      return {
        ...state,
        posts: state.city.filter((item) => item._id !== action.payload),
      };
    }
    default:
      return state;
  }
};
