import React from "react";
import { fetchCity } from "../../../redux/actions/actions";
import { useDispatch } from "react-redux";
import videoBg from "../../../assets/bgvideo.mp4";
import City from "../../City/City";

export default function LandingPage() {
  const [city, setCity] = React.useState("");
  const [saved, setSaved] = React.useState(true);
  const dispatch = useDispatch();

  const makeApiCall = (str: string) => {
    dispatch(fetchCity(str) as any);
  };

  return (
    <div className="overflow-y-hidden">
      <div className="flex items-center justify-center">
        <div className="bg-red-500 w-4/12 h-[500px]">
          <div className="flex border border-black w-4/12">
            <input
              className=" focus:outline-none bg-none"
              placeholder="Enter a city..."
              onChange={(e) => setCity(e.target.value)}
            />
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth={2}
              onClick={() => makeApiCall(city)}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
              />
            </svg>
          </div>
          <City />
        </div>
        <div className="w-8/12">
          <div className="bg-red-500 ">Lista</div>
        </div>
      </div>
    </div>
  );
}
