import React from "react";
import { fetchCity } from "../../../redux/actions/actions";
import { useDispatch } from "react-redux";
import cityBg from "../../../assets/photo-1477959858617-67f85cf4f1df.jpg";
import City from "../../City/City";
import SavedCityList from "../../SavedCityList/SavedCityList";

export default function LandingPage() {
  const [city, setCity] = React.useState("");
  const [saved, setSaved] = React.useState(true);
  const dispatch = useDispatch();

  const makeApiCall = (str: string) => {
    dispatch(fetchCity(str) as any);
  };

  return (
    <div className="flex items-center justify-center">
      <div className="flex flex-col">
        <div className="flex justify-evenly">
          <input
            className=" focus:outline-none py-1 px-3  "
            placeholder="Enter a city..."
            onChange={(e) => setCity(e.target.value)}
          />
          <button onClick={() => makeApiCall(city)}>Hola</button>
        </div>
        <div className="flex flex-col justify-center w-auto h-auto rounded-xl border border-white ">
          <City />
        </div>
      </div>
      <div className="w-8/12">
        <div className="bg-red-500 w-[800px] h-[800px]  ">
          <div className="flex">
            <h3>City</h3>
            <h3>Humidity</h3>
            <h3>Temp</h3>
          </div>
          <SavedCityList />
        </div>
      </div>
    </div>
  );
}
