import React from "react";
import { fetchCity } from "../../../redux/actions/actions";
import { useDispatch } from "react-redux";
import videoBg from "../../../assets/bgvideo.mp4";

export default function LandingPage() {
  const [city, setCity] = React.useState("");
  const [saved, setSaved] = React.useState(true);
  const dispatch = useDispatch();

  const makeApiCall = (str: string) => {
    dispatch(fetchCity(str) as any);
  };

  return (
    <div className="overflow-y-hidden">
      {/* <input placeholder="5000" onChange={(e) => setCity(e.target.value)} />
      <button onClick={() => makeApiCall(city)}>aaaaaaaaaaaaaaaaaaa</button> */}
      {/* <video
        className="bg-auto bg-center overflow-y-hidden mt-[-135px] opacity-50 relative"
        src={videoBg}
        autoPlay
        loop
        muted
      /> */}

      {saved ? <h1>CIudades guardadas</h1> : <h1>CIudades borradas</h1>}

      <div className="flex justify-center items-center w-full h-20 pt-4">
        <h1 className="text-2xl text-white font-bold">Areas</h1>
      </div>
      <div className="flex justify-center items-center text-kineduWhite">
        <button
          onClick={() => setSaved(true)}
          className={`border-2 border-r-0 w-7/12 rounded-l-full ${
            saved ? "bg-black text-white" : "text-black bg-white"
          } h-14 border-white ml-6 text-lg md:w-52 font-bold`}
        >
          Ciudades guardadas
        </button>
        <button
          onClick={() => setSaved(false)}
          className={`border-2  w-7/12 rounded-r-full h-14  ${
            saved ? "bg-white text-black" : " bg-black text-white"
          } border-white mr-6 text-lg md:w-52 font-bold`}
        >
          Ciudades borradas
        </button>
      </div>
    </div>
  );
}
