import { Dispatch } from "redux";
import axios from "axios";
import { url, apiKey } from "../../utils/utils";

import { ACTION_TYPES } from "../actionTypes/actionTypes";

const fetchPostsRequest = () => {
  return {
    type: ACTION_TYPES.FETCH_POSTS_REQUEST,
  };
};

const fetchPostsSuccess = (city: any[]) => {
  return {
    type: ACTION_TYPES.FETCH_POSTS_SUCCESS,
    payload: city,
  };
};

const fetchPostsFailure = (error: string) => {
  return {
    type: ACTION_TYPES.FETCH_POSTS_FAILURE,
    payload: error,
  };
};

const deleteOnePost = (id: string) => {
  return {
    type: ACTION_TYPES.DELETE_ONE_POST,
    payload: id,
  };
};

export const fetchCity = (str: string) => {
  return (dispatch: Dispatch) => {
    dispatch(fetchPostsRequest());
    axios
      .get(`${url}${str}${apiKey}`)
      .then((response) => {
        const city = response.data;
        dispatch(fetchPostsSuccess(city));
      })
      .catch((error) => {
        dispatch(fetchPostsFailure(error.message));
      });
  };
};

export const deletePost = (id: string) => {
  return (dispatch: Dispatch) => {
    dispatch(deleteOnePost(id));
    axios.post(`/saveCity/${id}`);
  };
};

export const saveCity = (payload: any[]) => {
  return (dispatch: Dispatch) => {
    axios({
      method: "post",
      url: "/saveCity",
      data: {
        firstName: "Finn",
        lastName: "Williams",
      },
    });
  };
};
